import React, { useState } from "react";
import { Input, Button, Spacer, Card, Radio } from "@nextui-org/react";
import "./SearchBar.scss";

export const countries = [
  { key: "taiwan", label: "Тайвань" },
  { key: "egypt", label: "Єгипет" },
  { key: "boston", label: "Бостон" },
  { key: "maldives", label: "Мальдіви" },
];

export const SearchBar: React.FC = () => {
  const [searchQuery, setSearchQuery] = useState<string>("");
  const [selectedCountry, setSelectedCountry] = useState<string>("taiwan");

  return (
    <Card className="search-bar">
      <div className="search-bar-container">
        {/* Левый блок с радио-кнопками */}
        <div className="radio-group">
          <h4 className="search-filter-title">Обрати країну</h4>
          {countries.map((country) => (
            <div key={country.key} className="radio-item">
              <Radio
                value={country.key}
                checked={selectedCountry === country.key}
                onChange={() => setSelectedCountry(country.key)}
              >
                {country.label}
              </Radio>
            </div>
          ))}
        </div>

        {/* Правый блок с поиском */}
        <div className="search-content">
          <h3 className="search-title">Знайдіть те, що шукаєте</h3>
          <Spacer y={1} />
          <Input
            isClearable
            fullWidth
            placeholder="Введіть запит"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            size="lg"
            className="search-input"
          />
          <Spacer y={1} />
          <Button
            onClick={() =>
              console.log(
                "Поиск:",
                searchQuery,
                "Выбрана країна:",
                selectedCountry
              )
            }
            color="primary"
            className="search-button"
          >
            Пошук
          </Button>
        </div>
      </div>
    </Card>
  );
};
