import {
  Modal,
  ModalContent,
  ModalHeader,
  ModalBody,
  ModalFooter,
  useDisclosure,
  Button,
  Input
} from "@nextui-org/react";
import { useState } from "react";

export const Form = () => {
  const { isOpen, onOpen, onOpenChange } = useDisclosure();
  const [phone, setPhone] = useState("+380");
  const [phoneError, setPhoneError] = useState("");

  const handlePhoneChange = (e: React.ChangeEvent<HTMLInputElement>) => {
      let value = e.target.value;

      // Гарантируем, что префикс остается
      if (!value.startsWith("+380")) {
          value = "+380";
      }

      // Удаляем все нецифровые символы кроме "+380"
      const digitsOnly = value.replace(/\D/g, "").slice(0, 12); // Ограничиваем до 12 символов

      // Проверка длины номера
      if (digitsOnly.length < 7) {
          setPhoneError("Номер повинен містити хоча б 7 цифр після +380");
      } else {
          setPhoneError("");
      }

      setPhone(`+${digitsOnly}`);
  };

  return (
      <div className="Form">
          <div className="Form-Button">
              <Button color="primary" variant="shadow" onPress={onOpen}>
                  Придбати
              </Button>
              <Modal isOpen={isOpen} placement="top-center" onOpenChange={onOpenChange}>
                  <ModalContent>
                      {(onClose) => (
                          <>
                              <ModalHeader className="flex flex-col gap-1">Log in</ModalHeader>
                              <ModalBody>
                                  <Input isRequired placeholder="Ваше ім'я" label="Ім'я" />
                                  <Input
                                      isRequired
                                      label="Номер телефону"
                                      value={phone}
                                      onChange={handlePhoneChange}
                                      isInvalid={!!phoneError}
                                      errorMessage={phoneError}
                                  />
                                  <Input label="Email" placeholder="Enter your email" variant="bordered" />
                                  <Input isRequired label="Кількість місць" defaultValue="1" />
                              </ModalBody>
                              <ModalFooter>
                                  <Button color="danger" variant="flat" onPress={onClose}>
                                      Відмінити
                                  </Button>
                                  <Button color="primary" onPress={onClose} isDisabled={!!phoneError}>
                                      Підтвердити
                                  </Button>
                              </ModalFooter>
                          </>
                      )}
                  </ModalContent>
              </Modal>
          </div>
      </div>
  );
};
