import {
  Modal,
  ModalContent,
  ModalHeader,
  ModalBody,
  ModalFooter,
  useDisclosure,
  Button,
  Input
} from "@nextui-org/react";
import { useState } from "react";

export const Form = () => {
  const { isOpen, onOpen, onOpenChange } = useDisclosure();

  const [name, setName] = useState("");
  const [nameError, setNameError] = useState("");

  const [phone, setPhone] = useState("+380");
  const [phoneError, setPhoneError] = useState("");

  const [seats, setSeats] = useState("1");
  const [seatsError, setSeatsError] = useState("");

  const MAX_SEATS = 10; // Укажи актуальное количество доступных мест

  // Валидация имени (только буквы и пробелы)
  const handleNameChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    if (!/^[a-zA-Zа-яА-ЯіІєЄґҐїЇ'’ ]*$/.test(value)) {
      setNameError("Ім'я може містити тільки літери та пробіли");
    } else {
      setNameError("");
    }
    setName(value);
  };

  // Валидация номера телефона (ровно 9 цифр после +380)
  const handlePhoneChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    let value = e.target.value;
    if (!value.startsWith("+380")) {
      value = "+380";
    }

    const digitsOnly = value.replace(/\D/g, "").slice(3); // Убираем "+380", оставляем цифры
    const formattedPhone = "+380" + digitsOnly.slice(0, 9); // Ограничиваем до 9 цифр

    if (digitsOnly.length < 9) {
      setPhoneError("Номер повинен містити 9 цифр після +380");
    } else {
      setPhoneError("");
    }

    setPhone(formattedPhone);
  };

  // Валидация количества мест
  const handleSeatsChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, ""); // Только цифры
    const intSeats = parseInt(value, 10) || 1;

    if (intSeats < 1) {
      setSeatsError("Мінімальна кількість місць — 1");
    } else if (intSeats > MAX_SEATS) {
      setSeatsError(`Максимальна кількість місць — ${MAX_SEATS}`);
    } else {
      setSeatsError("");
    }

    setSeats(intSeats.toString());
  };

  // Очистка формы
  const resetForm = () => {
    setName("");
    setNameError("");
    setPhone("+380");
    setPhoneError("");
    setSeats("1");
    setSeatsError("");
  };

  return (
      <div className="Form">
          <div className="Form-Button">
              <Button color="primary" variant="shadow" onPress={onOpen}>
                  Придбати
              </Button>
              <Modal isOpen={isOpen} placement="top-center" onOpenChange={(open) => {
                if (!open) resetForm(); // Очищаем форму при закрытии
                onOpenChange();
              }}>
                  <ModalContent>
                      {(onClose) => (
                          <>
                              <ModalHeader className="flex flex-col gap-1">Log in</ModalHeader>
                              <ModalBody>
                                  <Input 
                                      isRequired 
                                      placeholder="Ваше ім'я" 
                                      label="Ім'я" 
                                      value={name}
                                      onChange={handleNameChange}
                                      isInvalid={!!nameError}
                                      errorMessage={nameError}
                                  />
                                  <Input
                                      isRequired
                                      label="Номер телефону"
                                      value={phone}
                                      onChange={handlePhoneChange}
                                      isInvalid={!!phoneError}
                                      errorMessage={phoneError}
                                  />
                                  <Input label="Email" placeholder="Enter your email" variant="bordered" />
                                  <Input
                                      isRequired
                                      label="Кількість місць"
                                      value={seats}
                                      onChange={handleSeatsChange}
                                      isInvalid={!!seatsError}
                                      errorMessage={seatsError}
                                  />
                              </ModalBody>
                              <ModalFooter>
                                  <Button color="danger" variant="flat" onPress={onClose}>
                                      Відмінити
                                  </Button>
                                  <Button 
                                      color="primary" 
                                      onPress={onClose} 
                                      isDisabled={!!nameError || !!phoneError || !!seatsError}
                                  >
                                      Підтвердити
                                  </Button>
                              </ModalFooter>
                          </>
                      )}
                  </ModalContent>
              </Modal>
          </div>
      </div>
  );
};
