import { Swiper as SwiperBase, SwiperSlide } from 'swiper/react';
import { Pagination, A11y, Autoplay } from 'swiper/modules';
import 'swiper/css/effect-fade';
import { Button } from '@nextui-org/react';
import { useQuery } from '@tanstack/react-query';
import 'swiper/css';
import 'swiper/css/pagination';
import { Loader } from '../../../Main/components/Loader/Loader';

interface Tour {
    id           :number   
    title        :string  
    description  :string  
    callToAction :string  
    imageSrc     :string  
  }
export const Swiper: React.FC =()=>{
    const { isPending, error, data } = useQuery({
        queryKey: ['toursData'],
        queryFn: async() => {
          const fetched = await fetch('/api/tourswiper');
          const tours: Tour[] = await fetched.json();
          return tours;
        },
      })

      if (isPending) {
        return <Loader />
      }

      if (error) {
        return <p>Error</p>;
      }

    return(
        <SwiperBase
        modules={[Pagination, A11y, Autoplay]}
        slidesPerView={1}
        navigation
        pagination={{ clickable: true }}
        className={'Swiper'}
        autoplay={{ delay: 5000 }}
        effect='cards'
      >
        {data.slice(0,4).map(tour => (
          <SwiperSlide key={tour.id} className={'Swiper-Slide'} style={{backgroundImage: `url("http://localhost:1323${tour.imageSrc}")`}}>

          </SwiperSlide>
        ))}
      </SwiperBase>
    );
}
