import { Card, CardBody, CardHeader } from "@nextui-org/react";
import { useQuery } from "@tanstack/react-query";
import { useParams } from "react-router-dom"; // Используем для получения id из URL

interface Tour {
  id: number;
  title: string;
  status: string;
  detailed_description: string;
  rating: number;
  country: string;
  date_from: Date;
  date_to: Date;
  duration: number;
}

export const InfoSide = () => {
  const { id } = useParams(); // Получаем ID из URL

  const { isPending, error, data } = useQuery({
    queryKey: ["tourData", id], // Ключ кеша зависит от id
    queryFn: async () => {
      const fetched = await fetch(`/api/tours/${id}`);
      if (!fetched.ok) throw new Error("Tour not found");
      const rawTour: any = await fetched.json();

      return {
        ...rawTour,
        date_from: new Date(rawTour.date_from),
        date_to: new Date(rawTour.date_to),
      };
    },
    enabled: !!id, // Запрос выполняется только если есть id
  });

  if (isPending) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;
  if (!data) return <p>Tour not found</p>;

  return (
    <div className="InfoSide">
      <Card key={data.id}>
        <CardHeader>
          <h2>{data.title}</h2>
        </CardHeader>
        <CardBody>
          <h3>{data.country}</h3>
          <h3>Rating: {data.rating}</h3>
          <h3>
            {data.date_from.toLocaleDateString()} -{" "}
            {data.date_to.toLocaleDateString()}
          </h3>
          <h3>Duration: {data.duration} days</h3>
          <h3>{data.detailed_description}</h3>
        </CardBody>
      </Card>
    </div>
  );
};
