import {
  Modal,
  ModalContent,
  ModalHeader,
  ModalBody,
  ModalFooter,
  useDisclosure,
  Button,
  Input
} from "@nextui-org/react";
import { useQuery } from '@tanstack/react-query';
import { useParams } from "react-router-dom"; 
import { useState } from "react";

interface TourSeats{
  ID : number
  TourDateID : number
  AvailableSeats : number
}

export const Form = () => {
  const { isOpen, onOpen, onOpenChange } = useDisclosure();

  const [name, setName] = useState("");
  const [nameError, setNameError] = useState("");

  const [phone, setPhone] = useState("+380");
  const [phoneError, setPhoneError] = useState("");

  const [seats, setSeats] = useState("1");
  const [seatsError, setSeatsError] = useState("");
  const { id } = useParams(); 
  const { isPending, error, data } = useQuery({
    queryKey: ['tourSeatsData',id],
    queryFn: async() => {
      const fetched = await fetch(`/api/tour-seats/${id}`);
      const tours: TourSeats[] = await fetched.json();
      return tours;
    },
  })

if (isPending) {
    return <p>Loading..</p>
  }

  if (error) {
    return <p>Error</p>;
  }
  const MAX_SEATS = data.length > 0 ? data[0].AvailableSeats : 0;



  const handleNameChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    if (!/^[a-zA-Zа-яА-ЯіІєЄґҐїЇ'’ ]*$/.test(value)) {
      setNameError("Ім'я може містити тільки літери та пробіли");
    } else {
      setNameError("");
    }
    setName(value);
  };


  const handlePhoneChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    let value = e.target.value;
    if (!value.startsWith("+380")) {
      value = "+380";
    }

    const digitsOnly = value.replace(/\D/g, "").slice(3); 
    const formattedPhone = "+380" + digitsOnly.slice(0, 9); 

    if (digitsOnly.length < 9) {
      setPhoneError("Номер повинен містити 9 цифр після +380");
    } else {
      setPhoneError("");
    }

    setPhone(formattedPhone);
  };

  // Валидация количества мест
  const handleSeatsChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, ""); 
    const intSeats = parseInt(value, 10) || 1;

    if (intSeats < 1) {
      setSeatsError("Мінімальна кількість місць — 1");
    } else if (intSeats > MAX_SEATS) {
      setSeatsError(`Максимальна кількість місць — ${MAX_SEATS}`);
    } else {
      setSeatsError("");
    }

    setSeats(intSeats.toString());
  };

  // Очистка формы
  const resetForm = () => {
    setName("");
    setNameError("");
    setPhone("+380");
    setPhoneError("");
    setSeats("1");
    setSeatsError("");
  };

  return (
      <div className="Form">
          <div className="Form-Button">
              <Button color="primary" variant="shadow" onPress={onOpen}>
                  Придбати
              </Button>
              <Modal isOpen={isOpen} placement="top-center" onOpenChange={(open) => {
                if (!open) resetForm(); 
                onOpenChange();
              }}>
                  <ModalContent>
                      {(onClose) => (
                          <>
                              <ModalHeader className="flex flex-col gap-1">Замовлення</ModalHeader>
                              <ModalBody>
                                  <Input 
                                      isRequired 
                                      placeholder="Ваше ім'я" 
                                      label="Ім'я" 
                                      value={name}
                                      onChange={handleNameChange}
                                      isInvalid={!!nameError}
                                      errorMessage={nameError}
                                  />
                                  <Input
                                      isRequired
                                      label="Номер телефону"
                                      value={phone}
                                      onChange={handlePhoneChange}
                                      isInvalid={!!phoneError}
                                      errorMessage={phoneError}
                                  />
                                  <Input label="Email" placeholder="Enter your email" variant="bordered" />
                                  <Input
                                      isRequired
                                      label="Кількість місць"
                                      value={seats}
                                      onChange={handleSeatsChange}
                                      isInvalid={!!seatsError}
                                      errorMessage={seatsError}
                                  />
                              </ModalBody>
                              <ModalFooter>
                                  <Button color="danger" variant="flat" onPress={onClose}>
                                      Відмінити
                                  </Button>
                                  <Button 
                                  color="primary" 
                                  onPress={onClose} 
                                  isDisabled={!name.trim() || !phone.trim() || !seats.trim() || !!nameError || !!phoneError || !!seatsError}
                                    >
                                      Підтвердити
                                  </Button>
                              </ModalFooter>
                          </>
                      )}
                  </ModalContent>
              </Modal>
          </div>
      </div>
  );
};
