import {Card, CardBody, CardHeader} from "@nextui-org/react";
import { useQuery } from "@tanstack/react-query";


interface Tour {
    id                    :number   
    title                 :string  
    status                :string
    detailed_description  :string  
    raiting               :number
    country               :string
    date_from             :Date
    date_to               :Date
    duration              :number
  }

export const InfoSide =()=>{

    const {isPending,error,data} = useQuery({
        queryKey : ['toursData'],
        queryFn: async()=>{
          const fetched = await fetch('/api/tours');
          const tours:Tour[] = await fetched.json();
          return tours;
        },
      })
      if (isPending) return <p>Loading...</p>;
      if (error) return <p>Error: {error.message}</p>;
    return (
        <div className="InfoSide">
            <Card>
                <CardHeader>
                   <h2>Tour.title</h2> 
                </CardHeader>
             <CardBody>
                <h3>Tour.country</h3>
                <h3>Tour.raiting</h3>
                <h3>Tour.date_from - Tour.date_to</h3>
                <h3>Tour.duration</h3>
                <h3>Tour.detailed_description</h3>
             </CardBody>
            </Card> 
        </div>
    )
}
