import React, { useState } from "react";
import { Input, Button, Spacer, Card, Radio } from "@nextui-org/react";
import "./SearchBar.scss";

export const SearchBar: React.FC = () => {
  const [searchQuery, setSearchQuery] = useState<string>("");
  const [searchFilter, setSearchFilter] = useState<string>("all");

  return (
    <Card className="search-bar">
      <div className="search-bar-body">
        <h3 className="search-title">Найдите то, что вам нужно</h3>
        <Spacer y={1} />
        <Input
          isClearable
          fullWidth
          placeholder="Введите запрос"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          size="lg"
          className="search-input"
        />
        <Spacer y={1} />
        <h4 className="search-filter-title">Выберите фильтр</h4>
        <div className="radio-group">
          <Radio
            value="all"
            checked={searchFilter === "all"}
            onChange={() => setSearchFilter("all")}
          >
            Все
          </Radio>
          <Radio
            value="tours"
            checked={searchFilter === "tours"}
            onChange={() => setSearchFilter("tours")}
          >
            Туры
          </Radio>
          <Radio
            value="hotels"
            checked={searchFilter === "hotels"}
            onChange={() => setSearchFilter("hotels")}
          >
            Отели
          </Radio>
          <Radio
            value="flights"
            checked={searchFilter === "flights"}
            onChange={() => setSearchFilter("flights")}
          >
            Авиарейсы
          </Radio>
        </div>
        <Spacer y={1} />
        <Button
          onClick={() =>
            console.log("Поиск:", searchQuery, "Фильтр:", searchFilter)
          }
          color="primary"
          className="search-button"
        >
          Поиск
        </Button>
      </div>
    </Card>
  );
};
