import React, { useState } from "react";
import { Input, Button, Spacer, Card, Radio, RadioGroup } from "@nextui-org/react";
import "./SearchBar.scss";

export const SearchBar: React.FC = () => {
  const [searchQuery, setSearchQuery] = useState<string>("");
  const [selectedCity, setSelectedCity] = useState<string>("");

  const handleCityChange = (value: string) => {
    setSelectedCity(value);
  };

  const handleSearch = () => {
    console.log("Поиск:", searchQuery, "Город:", selectedCity);
  };

  return (
    <Card className="search-bar">
      <div className="search-bar-body">
        <h3 className="search-title">Найдите то, что вам нужно</h3>
        <Spacer y={1} />
        <Input
          isClearable
          fullWidth
          placeholder="Введите запрос"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          size="lg"
          className="search-input"
        />
        <Spacer y={1} />
        <Button
          onClick={handleSearch}
          color="primary"
          className="search-button"
        >
          Поиск
        </Button>
        <Spacer y={1} />
        <RadioGroup
          color="warning"
          label="Выберите ваш любимый город"
          value={selectedCity}
          onChange={handleCityChange}
        >
          <Radio description="The capital of Argentina" value="buenos-aires">
            Buenos Aires
          </Radio>
          <Radio description="The capital of Australia" value="canberra">
            Canberra
          </Radio>
          <Radio description="The capital of England" value="london">
            London
          </Radio>
          <Radio description="The capital of Japan" value="tokyo">
            Tokyo
          </Radio>
        </RadioGroup>
      </div>
    </Card>
  );
};
