import {CheckboxGroup, Checkbox, Slider, SliderValue, Input} from "@nextui-org/react";
import "./SideBar.scss";
import React from "react";

export const SideBar =()=>{
    const minValue = 0
    const maxvalue = 20000
    const [value, setValue] = React.useState<SliderValue>([minValue, maxvalue]);
    return(

        <div className="SideBar">
        <CheckboxGroup label="Оберіть тур">
        <Checkbox value="buenos-aires">Buenos Aires</Checkbox>
        <Checkbox value="sydney">Sydney</Checkbox>
        <Checkbox value="san-francisco">San Francisco</Checkbox>
        <Checkbox value="london">London</Checkbox>
        <Checkbox value="tokyo">Tokyo</Checkbox>
      </CheckboxGroup>
      <div className="SideBar-slider">
        <div className="SliderBar-inputs">
            <Input size="sm" label={""}></Input>
        </div>
        <Slider
          label="Ціна"
          maxValue={maxvalue}
          minValue={minValue}
          step={0.01}
          size="sm"
          formatOptions={{style: "currency", currency: "UAH"}}
          value={value}
          onChange={setValue}
        />
      </div>
    </div>
    );
}
