import React, { useState } from "react";
import { Input, Button, Spacer, Card, Select, SelectItem } from "@nextui-org/react";
import "./SearchBar.scss";

export const countries = [
    {key: "taiwan", label: "Тайвань"},
    {key: "egypt", label: "Єгипет"},
    {key:"boston",label:"Бостон"},
    {key:"maldives",label:"Мальдіви"}
  ];



export const SearchBar: React.FC = () => {
  const [searchQuery, setSearchQuery] = useState<string>("");

  return (
    <Card className="search-bar">
      <div className="search-bar-body">
        <h3 className="search-title">Знайдіть те, що шукаєте</h3>
        <Spacer y={1} />
        <Input
          isClearable
          fullWidth
          placeholder="Введіть запит"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          size="lg"
          className="search-input"
        />
        <Spacer y={1} />
        <Button
          onClick={() => console.log("Поиск:", searchQuery)}
          color="primary"
          className="search-button"
        >
          Пошук
        </Button>
        {/* <Select
      className="max-w-xs"
      label="Країна"
      placeholder="Оберіть країну"
      selectionMode="multiple"
    >
      {countries.map((country) => (
        <SelectItem key={country.key}>{country.label}</SelectItem>
      ))}
    </Select> */}
      </div>
    </Card>
  );
};
